            newSections.Add(new PageSectionType { PageSectionTypeName = "Highlight", PageSectionTypeCategory = PageSectionTypeCategory.Misc, PageSectionTypeOrder = 3, PageSectionTypeBody = "<section id=\"section-<sectionId>\" class=\"highlight height-standard background-parallax\" style=\"background-size: cover;\"><div id=\"component-container-<componentStamp>-<sectionId>\" class=\"vertical-alignment component-container\"><h2 id=\"title-<componentStamp>-<sectionId>\" style=\"color: rgb(255, 255, 255);\">Lorem ipsum dolor sit amet, eu qui accusam scriptorem.</h2><p id=\"text-1-<componentStamp>-<sectionId>\" style=\"color: rgb(255, 255, 255);\">Lorem ipsum dolor sit amet, eu qui accusam scriptorem. Purto erat facilisis sea id, vulputate deseruisse eos te, errem doctus feugiat te prim.</p><p id=\"text-2-<componentStamp>-<sectionId>\" style=\"color: rgb(255, 255, 255);\">Lorem ipsum dolor sit amet, eu qui accusam scriptorem. Purto erat facilisis sea id, vulputate deseruisse eos te, errem doctus feugiat te prim.</p></div><div id=\"overlay-<componentStamp>-<sectionId>\" class=\"overlay overlay-medium\"></div></section>" });
            newSections.Add(new PageSectionType { PageSectionTypeName = "Quote", PageSectionTypeCategory = PageSectionTypeCategory.Misc, PageSectionTypeOrder = 4, PageSectionTypeBody = "<section id=\"section-<sectionId>\" class=\"quote\"><div class=\"quote-image-left\"></div><div class=\"quote-image-right\"></div><div id=\"container-<componentStamp>-<sectionId>\" class=\"vertical-alignment component-container\"><h2 id=\"quote-<componentStamp>-<sectionId>\">Perfection is not attainable, but if we chase perfection we can catch.</h2><p id=\"component-<componentStamp>-<sectionId>\">Tom McClean</p></div></section>" });
            newSections.Add(new PageSectionType { PageSectionTypeName = "Photo Gallery", PageSectionTypeCategory = PageSectionTypeCategory.Misc, PageSectionTypeOrder = 5, PageSectionTypeBody = "<section id=\"section-<sectionId>\" class=\"gallery height-standard background-parallax\"><div id=\"image-1-<componentStamp>-<sectionId>\" class=\"image\" style=\"background-image: url('/Areas/PageBuilder/Content/Images/Sample/Sample-1.jpg');\"></div><div id=\"image-2-<componentStamp>-<sectionId>\" class=\"image\" style=\"background-image: url('/Areas/PageBuilder/Content/Images/Sample/Sample-2.jpg');\"></div><div id=\"image-3-<componentStamp>-<sectionId>\" class=\"image\" style=\"background-image: url('/Areas/PageBuilder/Content/Images/Sample/Sample-3.jpg');\"></div><div id=\"image-4-<componentStamp>-<sectionId>\" class=\"image\" style=\"background-image: url('/Areas/PageBuilder/Content/Images/Sample/Sample-4.jpg');\"></div></section>" });

            foreach (var section in newSections)
            {
                section.PageSectionTypeBody = Regex.Replace(section.PageSectionTypeBody, @"-\d{13}-", "-<componentStamp>-");

                section.PageSectionTypeBody = ResolveDuplicateIdentifiers(section.PageSectionTypeBody);
            }

            foreach (var existingSection in context.PageSectionTypes.ToList())
            {
                var matchedTemplate = newSections.FirstOrDefault(x => x.PageSectionTypeName == existingSection.PageSectionTypeName);

                if (matchedTemplate == null) continue;

                if (existingSection.PageSectionTypeBody != matchedTemplate.PageSectionTypeBody)
                    context.PageSectionTypes.Remove(existingSection);
                else
                    newSections.Remove(matchedTemplate);
            }

            context.PageSectionTypes.AddRange(newSections);
        }

        private static string ResolveDuplicateIdentifiers(string htmlBody)
        {
            var document = new HtmlDocument();

            document.LoadHtml(htmlBody);

            var uniqueElementId = 0;

            foreach (var child in document.DocumentNode.Descendants())
            {
                if (child.NodeType != HtmlNodeType.Element)
                    continue;

                uniqueElementId += 1;

                if (child.Name.Equals("section", System.StringComparison.OrdinalIgnoreCase))
                {
                    child.SetAttributeValue("id", $"section-<sectionId>");
                }
                else
                {
                    child.SetAttributeValue("id", $"element-{uniqueElementId}-<componentStamp>-<sectionId>");
                }
            }

            return document.DocumentNode.OuterHtml;
        }
    }
