$(document).ready(function(){$('[data-toggle="popover"]').popover();$("body").on("click",function(n){$(n.target).data("toggle")!=="popover"&&$(n.target).parents(".popover.in").length===0&&$('[data-toggle="popover"]').popover("hide")});$('[data-toggle="confirmation"]').confirmation({href:function(n){return $(n).attr("href")}})});